void dma_block_input(unsigned int dma_ptr, unsigned int len)
{
  // put the value of pass_fail_0 into the
  // vector memory at a high address
  unsigned int occupancy;

  while(1)
  {
   CSR_READ(DMA_0_SCHEDULE_OCCUPANCY, occupancy);
   if(occupancy < 4)
   {
    break;
   }
  }


  CSR_WRITE(DMA_0_START_ADDR, dma_ptr);
  CSR_WRITE(DMA_0_LENGTH, len);
  CSR_WRITE(DMA_0_TIMER_VAL, 0xffffffff);  // start right away
  CSR_WRITE(DMA_0_PUSH_SCHEDULE, 0); // any values
}


// returns 0 for pass, 1 or more for failure
unsigned int compare(unsigned int* cpu_ptr1, unsigned int* cpu_ptr2, unsigned int len)
{
  unsigned int result = 0;
  for(unsigned int i = 0; i < len; i++) {
    if(cpu_ptr1[i] != cpu_ptr2[i]) {
      return i+1;
    }

    // if( i % 512 == 0) {
    //   ring_block_send_eth(i);
    // }
  }

  return result;
}

